<script lang="ts">
	import { Notyf } from 'notyf';
	import { createEventDispatcher, onDestroy, onMount } from 'svelte';
	import { quintInOut } from 'svelte/easing';
	import { fade } from 'svelte/transition';
	import Spinner from './Spinner.svelte';

	enum JobStatus {
		UNINITIALIZED = 'Uninitialized',
		PENDING = 'Pending',
		METASCHEDULED = 'MetaScheduled',
		SCHEDULED = 'Scheduled',
		RUNNING = 'Running',
		FINISHED = 'Finished',
		FAILED = 'Failed'
	}

	let notyf: Notyf;

	export let demoApiHost = 'demo-api.deepsquare.run';
	export let jobType = 'tgi';

	const dispatch = createEventDispatcher();
	let hasSubmitted = false;

	$: if (hasSubmitted) {
		dispatch('submit', hasSubmitted);
	}

	let urls: string[] = [];
	let lines: string[] = [];
	let socket: WebSocket;

	let oldJobStatus: JobStatus = JobStatus.UNINITIALIZED;
	let jobStatus: JobStatus = JobStatus.UNINITIALIZED;
	let buttonLabel = 'Submit Job';

	// The button label changes depending on the job status
	$: if (hasSubmitted && jobStatus !== JobStatus.RUNNING) {
		buttonLabel = 'Submitting...';
	} else if (jobStatus === JobStatus.RUNNING && urls.length === 0) {
		buttonLabel = 'Wait for the App to be ready...';
	} else if (urls.length > 0) {
		buttonLabel = 'Running...';
	} else {
		buttonLabel = 'Submit Job';
	}

	// Update the lines of the "terminal" when the job status changes
	$: {
		if (oldJobStatus !== jobStatus) {
			oldJobStatus = jobStatus;
			switch (jobStatus) {
				case JobStatus.PENDING:
					lines = [...lines, 'Job submitted to the DeepSquare Grid'];
					break;
				case JobStatus.METASCHEDULED:
					lines = [...lines, 'A cluster has been found!'];
					break;
				case JobStatus.SCHEDULED:
					lines = [...lines, 'Job queued. Allocating resources'];
					break;
				case JobStatus.RUNNING:
					lines = [...lines, 'Job is starting! Please wait for the application to be ready'];
					break;
				default:
					// Job has ended.
					jobStatus = JobStatus.UNINITIALIZED;
					oldJobStatus = JobStatus.UNINITIALIZED;
					urls = [];
					lines = [];
					hasSubmitted = false;
					if (socket) socket.close();
					notyf.success('The session has ended!\nThank you for trying DeepSquare!');
					break;
			}
		}

		if (urls.length > 0) {
			lines = [...lines, 'Application is ready!'];
		}
	}

	async function submitJob() {
		if (hasSubmitted) {
			return;
		}
		try {
			// Submit the job to the DeepSquare Grid
			hasSubmitted = true;
			const response = await fetch(`https://${demoApiHost}/job?job=${jobType}`, {
				method: 'POST'
			});
			const body = await response.text();
			if (!response.ok) {
				throw new Error(`Failed to submit job: ${body}`);
			}

			// Close the previous socket if it exists
			if (socket) {
				socket.close();
			}

			// Open a new WebSocket to receive job status updates
			socket = new WebSocket(`wss://${demoApiHost}/job?token=${body}`);
			socket.addEventListener('message', (event) => {
				try {
					const o = JSON.parse(event.data);
					if (o.type === 'job_status') {
						jobStatus = o.data;
					} else if (o.type === 'url') {
						urls = [...urls, ...o.data];
					}
				} catch (error) {
					console.error('Error:', error);
					notyf.error('Error: ' + error);
				}
			});
		} catch (error) {
			console.error('Error:', error);
			notyf.error('Error: ' + error);
		}

		// Event handler for errors
		socket.addEventListener('error', (event) => {
			console.error('WebSocket error:', event);
			notyf.error('WebSocket error: ' + event);
		});
	}

	// SVG animation
	let submittedCircle: SVGCircleElement;
	let metaScheduledCircle: SVGCircleElement;
	let scheduledCircle: SVGCircleElement;
	let submittedCircleAnimation: SVGAnimateTransformElement;
	let metaScheduledCircleAnimation: SVGAnimateTransformElement;
	let scheduledCircleAnimation: SVGAnimateTransformElement;
	$: {
		switch (jobStatus) {
			case JobStatus.PENDING:
				submittedCircle.setAttribute('opacity', '1');
				submittedCircleAnimation.beginElement();
				setTimeout(() => {
					submittedCircle.setAttribute('opacity', '0');
				}, 600);
				break;
			case JobStatus.METASCHEDULED:
				metaScheduledCircle.setAttribute('opacity', '1');
				metaScheduledCircleAnimation.beginElement();
				setTimeout(() => {
					metaScheduledCircle.setAttribute('opacity', '0');
				}, 600);
				break;
			case JobStatus.SCHEDULED:
				scheduledCircle.setAttribute('opacity', '1');
				scheduledCircleAnimation.beginElement();
				setTimeout(() => {
					scheduledCircle.setAttribute('opacity', '0');
				}, 600);
				break;
		}
	}

	// Load notyf only on this component.
	//
	// NOTE: You could also load it "app" level instead of component level if needed.
	onMount(() => {
		notyf = new Notyf({
			duration: 10000,
			ripple: false,
			dismissible: true
		});
	});

	onDestroy(() => {
		if (socket) {
			socket.close();
		}
	});
</script>

